import React, { Component } from 'react';
import Input from '../../components/input/index';
import Logo from '../../assets/img/logoCiti.png';
import './style.css'
// import { runInThisContext } from 'vm';
// import { threadId } from 'worker_threads';

class Form extends Component {
    constructor(props){
        super(props)
        this.state = {
            name:"",
            nameColor: false,
            lastName: "",
            lastNameColor: false,
            email: "",
            emailColor: false,
            password: "",
            passwordColor: false,
            password2:"",
            password2Color: false,
            street: "",
            streetColor: false,
            numExt: "",
            numInt: "",
            colony: "",
            CP: "",
            phone:"",
            ID: ""
        }
    }

    handleNameChange = (e) => {
        this.setState({ name: e.target.value });
        if(this.state.name.length > 1){
            this.setState({nameColor: true})
        }
    }

    handleLastNameChange = (e) => {
        this.setState({ lastName: e.target.value });
        if(this.state.lastName.length > 1){
            this.setState({lastNameColor: true})
        }
    }

    handleEmailChange = (e) => {
        this.setState({ email: e.target.value });
        if(this.state.email.length > 1){
            this.setState({emailColor: true})
        }
    }

    handlePasswordChange = (e) => {
        this.setState({ password: e.target.value });
        if(this.state.password.length > 5){
            this.setState({passwordColor: true})
        }
    }

    handleChangeStreet = (e) => {
        this.setState({street: e.target.value});
        if(this.state.street > 2){
            this.setState({streetColor: true})
        }
    }

    handlePassword2Change = (e) => {
        this.setState({ password2: e.target.value });
        if(this.state.password2 !== this.state.password){
            console.log('Tus contrase√±as no son iguales')
        }
        if(this.state.password === this.state.password2){
            this.setState({password2Color: true})
        }
    }
    render() {
        return (
            <main className="form">
                <header className="header-form">
                    <img src={Logo} alt="logo"/>
                    <h2>Registra tus datos</h2>
                </header>
                <div className="ball-container">
                    <span className="filled ball"></span>
                    <span className="ball"></span>
                    <span className="ball"></span>
                </div>
                <section>
                    <Input
                        label="Nombre"
                        type="text"
                        name="name"
                        value={this.state.name}
                        onChange={this.handleNameChange}
                        colorCheck={this.state.nameColor}
                    />
                    <Input
                        label="Apellidos"
                        type="text"
                        name="lastname"
                        value={this.state.lastName}
                        onChange={this.handleLastNameChange}
                        colorCheck={this.state.lastNameColor}
                    />
                    <Input
                        label="Correo electr√≥nico"
                        type="email"
                        name="email"
                        value={this.state.email}
                        onChange={this.handleEmailChange}
                        colorCheck={this.state.emailColor}
                    />
                    <div className="select-container">
                        <Input
                            label="Contrase√±a"
                            type="password"
                            name="password"
                            value={this.state.password}
                            onChange={this.handlePasswordChange}
                            colorCheck={this.state.passwordColor}
                        />
                        <Input
                            label="Confirmar contrase√±a"
                            name="password"
                            type="password"
                            value={this.state.password2}
                            onChange={this.handlePassword2Change}
                            colorCheck={this.state.password2Color}
                        />
                    </div>
                    <Input
                        label="Calle o Avenida"
                        type="text"
                        name="name"
                        value={this.state.street}
                        onChange={this.handleChangeStreet}
                        colorCheck={this.state.streetColor}

                    />
                    <div className="select-container">
                        <Input
                            label="N√∫mero ext."
                            type="number"
                            name="name"
                            value={this.state.numExt}
                        />
                        <Input
                            label="N√∫mero int."
                            type="number"
                            name="name"
                        />
                    </div>
                    <div className="select-container">
                        <Input
                            label="Fraccionamiento o colonia"
                            type="text"
                            name="name"
                        />
                        <Input
                            label="C.P."
                            type="number"
                            name="name"
                        />
                    </div>
                    <Input
                        label="Tel√©fono"
                        name="phone"
                        type="number"
                    />
                    <div className="select-container">
                        <select name="genero" id="" className="drops">
                                <option value="">Selecciona tu Identificaci√≥n</option>
                                <option value="">INE</option>
                                <option value="">IFE</option>
                                <option value="">Licencia para conducir</option>
                                <option value="">Pasaporte mexicano</option>
                        </select>
                        <Input
                            label="Numero de identificaci√≥n"
                            type="number"
                            name="ine"
                            value={this.state.ID}
                        />
                    </div>
                    <Input
                        label="CURP"
                        type="text"
                        name="name"
                    />
                    <p className="curp">Si no conoces tu CURP, puedes obtenerlo en esta <a href="https://www.gob.mx/curp/" target="_blank">liga</a> </p>                    
                    <div className="select-container">
                        <select name="genero" id="" className="drops">
                            <option value="">Selecciona tu g√©nero</option>
                            <option value="">Hombre</option>
                            <option value="">Mujer</option>
                            <option value="">Prefiero no decirlo</option>
                        </select>
                        <select name="occupation" id="" className="drops">
                        <option value="">Selecciona tu ocupaci√≥n</option>
                            <option value="">Estudiante</option>
                            <option value="">Empleado</option>
                            <option value="">Desempleado</option>
                            <option value="">Prefiero no decirlo</option>
                        </select>
                    </div>
                </section>
                <footer className="footer-form">
                    <button className="button back">Anterior</button>
                    <button className="button">Siguiente</button>
                </footer>
            </main>

        );
    }
}

export default Form;
